mapper.select:
select bk.id, bk.user_name as userName, bk.mobile_phone as mobilePhone, bk.sex, bk.surname_id as surnameId, bk.head_image as headImage, bk.office_phone as officePhone, bk.remark as remark, bk.app_id as appId, bk.create_time as createTime from weizu_address_book as bk where id = #{id}
and user_name = #{userName}
and bk.app_id = #{appId}

select bk.id, bk.user_name as userName, bk.mobile_phone as mobilePhone, bk.sex, bk.surname_id as surnameId, bk.head_image as headImage, bk.office_phone as officePhone, bk.remark as remark, bk.app_id as appId, bk.create_time as createTime from weizu_address_book bk left join weizu_surname su on bk.surname_id = su.id where 1=1
and su.id = #{surnameId}
and bk.user_name = #{userName}
and bk.mobile_phone = #{mobilePhone}
and bk.app_id = #{appId}
and (bk.user_name like CONCAT(CONCAT('%', #{searchKeyWord}),'%') or bk.remark like CONCAT(CONCAT('%', #{searchKeyWord}),'%')) limit 50

select bk.id, bk.user_name as userName, bk.mobile_phone as mobilePhone, bk.sex, bk.surname_id as surnameId, su.surname as surname, bk.head_image as headImage, bk.office_phone as officePhone, bk.remark as remark, bk.app_id as appId, bk.create_time as createTime from weizu_address_book bk left join weizu_surname su on bk.surname_id = su.id where 1 = 1
and bk.user_name LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%')
and bk.create_time >= #{pd.lastLoginStart}
and bk.create_time <= #{pd.lastLoginEnd}
and bk.app_id = #{pd.appId}
