mapper.update:
update  weizu_image_text
set modify_time = now()

    ,user_id = #{userId}


    ,nick_name = #{nickName}


    ,user_name = #{userName}


    ,user_mobile = #{userMobile}


    ,program_state = #{programState}


    ,title = #{title}


    ,content = #{content}


    ,first_path = #{firstPath}


    ,cloud_path = #{cloudPath}


    ,status = #{status}

where
id = #{id}

mapper.select:
select id as id, user_id as userId, nick_name as nickName, user_name as userName, user_mobile as userMobile, program_state as programState, title as title, content as content, first_path as firstPath, cloud_path as cloudPath, app_id as appId, status as status, create_time as createTime, modify_time as modifyTime from weizu_image_text where 1=1
and status = #{status}
and user_id = #{userId}

select id as id, user_id as userId, nick_name as nickName, user_name as userName, user_mobile as userMobile, program_state as programState, title as title, content as content, first_path as firstPath, cloud_path as cloudPath, app_id as appId, status as status, create_time as createTime, modify_time as modifyTime from weizu_image_text where 1=1
and app_id = #{appId}
and status = #{status}
and module = #{module}
and user_id = #{userId}
order by create_time desc limit #{startLimit}, #{endLimit}

select id as id, user_id as userId, title as title, nick_name as nickName, user_name as userName, user_mobile as userMobile, program_state as programState, content as content, first_path as firstPath, cloud_path as cloudPath, app_id as appId, status as status, create_time as createTime, modify_time as modifyTime from weizu_image_text where 1 = 1
and user_id LIKE CONCAT(CONCAT('%', #{pd.userId}),'%')
and create_time >= #{pd.lastLoginStart}
and create_time <= #{pd.lastLoginEnd}
and app_id = #{pd.appId}
