mapper.select:
select

from weizu_order
where 1=1

    and order_no = #{orderNo,jdbcType=VARCHAR}


    and user_id = #{userId,jdbcType=BIGINT}

select od.id as orderId, od.order_no as orderNo, od.user_id as userId, od.integral_num as orderIntegralNum, od.commodity_id as commodityId, od.commodity_num as orderCommodityNum, od.commodity_title as commodityTitle, od.nick_name as nickName, od.user_name as userName, od.exchange_status as exchangeStatus, od.exchange_emp as exchangeEmp, od.exchange_time as exchangeTime, od.create_time as createTime, od.modify_time as modifyTime, cd.integral_num as commodityIntegralNum, cd.first_path as commodityFirstImg from weizu_order od left join weizu_commodity cd on od.commodity_id=cd.id where 1=1
and od.order_no = #{orderNo,jdbcType=VARCHAR}
and od.user_id = #{userId,jdbcType=BIGINT}
and od.exchange_status = #{exchangeStatus,jdbcType=INTEGER}
