mapper.update:
update  weizu_user
	set modify_time = now()
	
		,avatar_url = #{avatarUrl}
	
	
		,city = #{city}
	
	
		,province = #{province}
	
	
		,country = #{country}
	
	
		,nick_name = #{nickName}
	
	
		,rights = #{rights}
	
	
		,manager_rights = #{managerRights}
	
	where 
		open_id = #{openId}

update  weizu_user
	set modify_time = now()
	
		,avatar_url = #{avatarUrl}
	
	
		,city = #{city}
	
	
		,province = #{province}
	
	
		,country = #{country}
	
	
		,nick_name = #{nickName}
	
	
		,rights = #{rights}
	
	
		,manager_rights = #{managerRights}
	
	where 
		id = #{id}

mapper.select:
select id as id, avatar_url as avatarUrl, city as city, country as country, gender as gender, language as language, nick_name as nickName, province as province, open_id as openId, rights as rights, create_time as createTime, modify_time as modifyTime from weizu_user where 1 = 1
and nick_name LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%')
and create_time >= #{pd.lastLoginStart}
and create_time <= #{pd.lastLoginEnd}
